import {
  PlaneGeometry,
  SphereGeometry,
  Mesh,
  PointLight,
  Scene,
  PerspectiveCamera,
  WebGLRenderer,
  Vector2,
  MeshBasicMaterial,
  VertexColors,
  Color
} from "three";

import KdTree from '../../js/KdTree'

const canvas = document.querySelector("#canvas");
const dpr = window.devicePixelRatio
const { offsetWidth: width, offsetHeight: height } = canvas;
const calculateViewportHeight = (perspectiveAngle, distance) => {
  return Math.tan(perspectiveAngle / 2 / 180 * Math.PI) * distance * 2;
};

const scene = new Scene();
const camera = new PerspectiveCamera(75, width / height, 0.1, 1000);
camera.position.z = 30;

const renderer = new WebGLRenderer({ canvas, alpha: true });
renderer.setPixelRatio(window.devicePixelRatio);
renderer.setSize(width, height);

const viewportHeight = calculateViewportHeight(75, 30);

const geometry = new PlaneGeometry(
  viewportHeight * .66,
  viewportHeight * .66,
  100,
  100
);
// geometry.__dirtyVertices = true;

const material = new MeshBasicMaterial({ vertexColors: VertexColors });
const plane = new Mesh(geometry, material);
plane.dynamic = true
plane.__dirtyColors = true
scene.add(plane);

const light = new PointLight(0xffffff, 1, 100);
light.position.set(20, 10, 30);
scene.add(light);

const particles = new Array(1000)
  .fill(0)
  .map(() => ({
    x: viewportHeight * -.33 + Math.random() * viewportHeight * .66, 
    y: viewportHeight * -.33 + Math.random() * viewportHeight * .66,
    dx: Math.random() / 60,
    dy: Math.random() / 60,
    color: Math.random() * 0xffffff
  }))

particles.forEach(particle => {
  const { x, y, color } = particle
  const geometry = new SphereGeometry(viewportHeight / height * 3, 2, 2)
  const material = new MeshBasicMaterial({ color });
  const sphere = new Mesh(geometry, material);
  particle.mesh = sphere
  sphere.position.x = x
  sphere.position.y = y
  scene.add(sphere);
})

const vertexTree = new KdTree(plane.geometry.vertices)

const updatePositions = () => {
  particles.forEach(particle => {
    particle.x += particle.dx
    particle.y += particle.dy
    particle.mesh.position.x = particle.x
    particle.mesh.position.y = particle.y
    particle.mesh.verticesNeedUpdate = true
  })
}

const updateColors = () => {

  plane.geometry.vertices.forEach(vertex => {
    vertex.color = null
  })

  particles.forEach(particle => {
    const vertex = vertexTree.nearestNeighbor(particle)
    const color = vertex.color || 0
    const particleCount = vertex.particleCount || 0
    vertex.color = (color * particleCount + particle.color) / (particleCount + 1)
    vertex.particleCount = particleCount + 1
  })

  plane.geometry.faces.forEach(face => {
    face.vertexColors[0] = new Color(plane.geometry.vertices[face.a].color || 0xffffff)
    face.vertexColors[1] = new Color(plane.geometry.vertices[face.b].color || 0xffffff)
    face.vertexColors[2] = new Color(plane.geometry.vertices[face.c].color || 0xffffff)
  })

  plane.geometry.colorsNeedUpdate = true
  plane.geometry.elementsNeedUpdate = true
}

const render = () => {
  requestAnimationFrame(render);
  updatePositions()
  // updateColors()
  renderer.render(scene, camera);
};

render();
